;---------------------------------------;FLIPOS.ASM - THIS FILE IS PART;OF THE √64 ≈MULATOR ‘EST ”UITE;PUBLIC DOMAIN, NO COPYRIGHT           *= $0801           .BYTE $4C,$14,$08,$00,$97TURBOASS   = 780           .TEXT "780"           .BYTE $2C,$30,$3A,$9E,$32,$30           .BYTE $37,$33,$00,$00,$00           .BLOCK           LDA #1           STA TURBOASS           LDX #0           STX $D3           LDA THISNAMEPRINTTHIS           JSR $FFD2           INX           LDA THISNAME,X           BNE PRINTTHIS           JSR MAIN           LDA #$37           STA 1           LDA #$2F           STA 0           JSR $FD15           JSR $FDA3           JSR PRINT           .TEXT " - OK"           .BYTE 13,0           LDA TURBOASS           BEQ LOADNEXT           JSR WAITKEY           JMP $8000           .BENDLOADNEXT           .BLOCK           LDX #$F8           TXS           LDA NEXTNAME           CMP #"-"           BNE NOTEMPTY           JMP $A474NOTEMPTY           LDX #0PRINTNEXT           JSR $FFD2           INX           LDA NEXTNAME,X           BNE PRINTNEXT           LDA #0           STA $0A           STA $B9           STX $B7           LDA #<NEXTNAME           STA $BB           LDA #>NEXTNAME           STA $BC           JMP $E16F           .BEND;---------------------------------------;PRINT TEXT WHICH IMMEDIATELY FOLLOWS;THE  ”“ AND RETURN TO ADDRESS AFTER 0PRINT           .BLOCK           PLA           STA NEXT+1           PLA           STA NEXT+2           LDX #1NEXT           LDA $1111,X           BEQ END           JSR $FFD2           INX           BNE NEXTEND           SEC           TXA           ADC NEXT+1           STA RETURN+1           LDA #0           ADC NEXT+2           STA RETURN+2RETURN           JMP $1111           .BEND;---------------------------------------;PRINT HEX BYTEPRINTHB           .BLOCK           PHA           LSR A           LSR A           LSR A           LSR A           JSR PRINTHN           PLA           AND #$0FPRINTHN           ORA #$30           CMP #$3A           BCC NOLETTER           ADC #6NOLETTER           JMP $FFD2           .BEND;---------------------------------------;WAIT UNTIL RASTER LINE IS IN BORDER;TO PREVENT GETTING DISTURBED BY ƒÕ¡SWAITBORDER           .BLOCK           LDA $D011           BMI OKWAIT           LDA $D012           CMP #30           BCS WAITOK           RTS           .BEND;---------------------------------------;WAIT FOR A KEY AND CHECK FOR ”‘œ–WAITKEY           .BLOCK           JSR $FD15           JSR $FDA3           CLIWAIT           JSR $FFE4           BEQ WAIT           CMP #3           BEQ STOP           RTSSTOP           LDA TURBOASS           BEQ LOAD           JMP $8000LOAD           JSR PRINT           .BYTE 13           .TEXT "BREAK"           .BYTE 13,0           JMP LOADNEXT           .BEND;---------------------------------------THISNAME   .NULL "FLIPOS"NEXTNAME   .NULL "ONESHOT"MAIN;---------------------------------------;SET ONESHOT AT UNDERFLOW-1           .BLOCK           SEI           LDA #0           STA $DC0E           STA $DC0F           LDA #$7F           STA $DC0D           BIT $DC0D           LDA #3           STA $DC04           LDA #0           STA $DC05           LDA #%00100001           STA $DC0E           LDA #255           STA $DC04           STA $DC05           LDA #%00000000           STA $DC0E           JSR WAITBORDER           LDA #%00000001           LDX #%00001001           STA $DC0E           STX $DC0E           LDA $DC04           AND $DC05           CMP #255           BEQ OK1           JSR PRINT           .BYTE 13           .TEXT "SET ONESHOT AT T-1 "           .TEXT "DID NOT STOP COUNTER"           .BYTE 0           JSR WAITKEYOK1           .BEND;---------------------------------------;SET ONESHOT AT UNDERFLOW           .BLOCK           SEI           LDA #0           STA $DC0E           STA $DC0F           LDA #$7F           STA $DC0D           BIT $DC0D           LDA #2           STA $DC04           LDA #0           STA $DC05           LDA #%00100001           STA $DC0E           LDA #255           STA $DC04           STA $DC05           LDA #%00000000           STA $DC0E           JSR WAITBORDER           LDA #%00000001           LDX #%00001001           STA $DC0E           STX $DC0E           LDA $DC04           AND $DC05           STA 16384           CMP #252           BEQ OK1           JSR PRINT           .BYTE 13           .TEXT "SET ONESHOT AT T "           .TEXT "MAY NOT STOP COUNTER"           .BYTE 0           JSR WAITKEYOK1           .BEND;---------------------------------------;CLEAR ONESHOT AT UNDERFLOW-1           .BLOCK           SEI           LDA #0           STA $DC0E           STA $DC0F           LDA #$7F           STA $DC0D           BIT $DC0D           LDA #3           STA $DC04           LDA #0           STA $DC05           LDA #%00100001           STA $DC0E           LDA #255           STA $DC04           STA $DC05           LDA #%00000000           STA $DC0E           JSR WAITBORDER           LDA #%00001001           LDX #%00000001           STA $DC0E           STX $DC0E           LDA $DC04           AND $DC05           CMP #255           BEQ OK1           JSR PRINT           .BYTE 13           .TEXT "CLR ONESHOT AT T-1 "           .TEXT "DID NOT STOP COUNTER"           .BYTE 0           JSR WAITKEYOK1           .BEND;---------------------------------------;CLEAR ONESHOT AT UNDERFLOW-2           .BLOCK           SEI           LDA #0           STA $DC0E           STA $DC0F           LDA #$7F           STA $DC0D           BIT $DC0D           LDA #4           STA $DC04           LDA #0           STA $DC05           LDA #%00100001           STA $DC0E           LDA #255           STA $DC04           STA $DC05           LDA #%00000000           STA $DC0E           JSR WAITBORDER           LDA #%00001001           LDX #%00000001           STA $DC0E           STX $DC0E           LDA $DC04           AND $DC05           CMP #254           BEQ OK1           JSR PRINT           .BYTE 13           .TEXT "CLR ONESHOT AT T-2 "           .TEXT "MAY NOT STOP COUNTER"           .BYTE 0           JSR WAITKEYOK1           .BEND;---------------------------------------           RTS